// App.js
import { useSelector, useDispatch } from "react-redux";
import { fetchData, postData, deletePost } from "./redux/reducer/fetchReducer";
import { useEffect, useState } from "react";
import style from "./app.module.css"

function App() {
  const dispatch = useDispatch();

  // Fetching the data
  useEffect(() => {
    dispatch(fetchData());
  }, [dispatch]);

  // Selectors
  const data = useSelector((state) => state.fetch.data);
  const allUniqueUsers = [...new Set(data.map(user => user.userId))];
  const selectedUser = useSelector((state) => state.user);
  const [todo, setTodo] = useState("");
  const [selectedUserId, setSelectedUserId] = useState('0');
  const [edit,setEdit]=useState(false);
  console.log(data.length+1);  


  // New Post
  const createPost = (e) => {
    e.preventDefault();
    dispatch(postData({  userId: Number(selectedUserId), id:data.length+1, title: todo, completed:false }));
    setTodo("");
  };

    data.map(u=>console.log(u));

  // Filter the data based on the selected user
  const filteredData = data.filter((user) => user.userId === Number(selectedUserId));
  // console.log(` All UNIQUE USERS FROM STORAGE ${allUniqueUsers}, SELECTED USER :${selectedUser}, SELECTED USER ID:${selectedUserId} `);


  //update post

  const handleEdit = (userId, id, todo, complete)=>{

   dispatch(updatePost(id,{userId,id,todo,complete}));
  }




  return (
    <div className={style.body}>
      <div className={style.container}>
        {/* Heading */}
        <div className={style.heading}>
          <h2>TodoList</h2>
          <div>
            <label>Select User: </label>
            <select value={selectedUserId} onChange={(e) => setSelectedUserId(e.target.value)}>
              <option value="0">All Users</option>
              {allUniqueUsers.map((userId) => (
                <option key={userId} value={userId}>{userId}</option>
              ))}
            </select>
          </div>
        </div>

        {/* Add Todo */}
        <div className="input-field">
          <form onSubmit={createPost}>
            <input
              type="text"
              value={todo}
              className={style.input}
              placeholder="Enter the todo"
              onChange={(e) => setTodo(e.target.value)}
            />
            <button type="submit">POST</button>
          </form>
        </div>

        {/* Todo list */}
        <div className={style.todoBox}>
          {filteredData.map((user) => (
            <div key={user.id} className={style.todoItem}>
              <h3>{user.title}</h3>
              <input 
                type="radio" 
                id={user.id} 
                value={user.completed} 
                checked={user.completed} 
                readOnly 
              />
              <label htmlFor={user.id}>Completed</label>
              <span>|</span>
              <img className={style.icon} src="/edit.png" onClick={()=>{setTodo(user.title), setEdit(true) }}/>
              <span>|</span>
              <img className={style.icon} src="/bin.png" onClick={()=>dispatch(deletePost(user.id))}/>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
