import { useSelector, useDispatch } from "react-redux";
import { fetchData } from "./redux/reducer/fetchReducer";
import { useEffect, useState } from "react";
import style from "./app.module.css";
import { addUser } from "./redux/reducer/userReducer";

function App() {
  const dispatch = useDispatch();

  // Fetching the data
  useEffect(() => {
    dispatch(fetchData());
  }, [dispatch]);

  // Selectors
  const data = useSelector((state) => state.fetch.data);
  const allUniqueUsers = [...new Set(data.map((user) => user.userId))];
  const selectedUser = useSelector((state) => state.user);

  // State for selected user
  const [selectedUserId, setSelectedUserId] = useState("0");

  // Function to change user
  const changeUser = (userId) => {
    dispatch(addUser(userId));
    setSelectedUserId(userId);
  };

  // Filter the data based on the selected user
  const filteredData = data.filter(
    (user) => user.userId === Number(selectedUserId)
  );

  return (
    <div className={style.body}>
      <div className={style.container}>
        {/* Heading */}
        <div className={style.heading}>
          <h2>TodoList</h2>
          <div>
            <label>Select User: </label>
            <select
              value={selectedUser}
              onChange={(e) => changeUser(e.target.value)}
            >
              {/* <option value="0">All Users</option> */}
              {allUniqueUsers.map((userId) => (
                <option key={userId} value={userId}>
                  {userId}
                </option>
              ))}
            </select>
          </div>
        </div>
        {/* add todo */}
        <div className="input-field">
          <input
            type="text"
            className={style.input}
            placeholder="Enter The todo"
          />{" "}
          <button>POST</button>
        </div>

        {/* Todo list */}
        <div className={style.todoBox}>
          {filteredData.map((user) => (
            <div key={user.id} className={style.todoItem}>
              <h3>{user.title}</h3>
              <input
                type="radio"
                id={user.id}
                value={user.completed}
                checked={user.completed}
                readOnly
              />
              <label htmlFor={user.id}>Completed</label>
              <span>|</span>
              <h4>Edit</h4>
              <span>|</span>
              <h4>Delete</h4>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
