import { useSelector, useDispatch } from "react-redux";
import { fetchData } from "./redux/reducer/fetchReducer";
import { useEffect, useState } from "react";
import style from "./app.module.css";
import { addUser } from "./redux/reducer/userReducer";

function App() {
  const dispatch = useDispatch();

  // Fetching the data
  useEffect(() => {
    dispatch(fetchData());
  }, [dispatch]);

  const data = useSelector((state) => state.fetch.data);
  const user = useSelector((state) => state.selectUser.userId);

  // Get all unique user IDs
  const allUniqueUsers = [...new Set(data.map(user => user.userId))];

  // State for selected user
  const [selectedUser, setSelectedUser] = useState('0');
  console.log(selectedUser);

  const changeUser = (user) => {
    dispatch(addUser(user));
    setSelectedUser(user);
  }

  // Filter the data based on the selected user
  const filteredData = data.filter((user) => user.userId === Number(selectedUser));
  console.log(filteredData);

  return (
    <div className={style.body}>
      <div className={style.container}>
        {/* heading */}
        <div className={style.heading}>
          <span>
            <h2>TodoList</h2>
          </span>
          <span>
            <label>Select User</label>
            <select 
              placeholder="Make a selection" 
              value={selectedUser} 
              onChange={(e) => changeUser(e.target.value)}
            >
              <option disabled>Select a user</option>
              {allUniqueUsers.map((u) => (
                <option key={u} value={u}>{u}</option>
              ))}
            </select>
          </span>
        </div>
        <div className={style.todoBox}>
          {/* todo list */}
          {filteredData.map((item) => (
            <div key={item.id} className={style.todoItem}>
              <p>{item.title}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
