import { useSelector, useDispatch } from "react-redux";
import { fetchData } from "./redux/reducer/fetchReducer";
import { useEffect } from "react";
import { useState } from "react";
import style from "./app.module.css";
import { addUser } from "./redux/reducer/userReducer";
function App() {
  const dispatch = useDispatch();

  //fetching the data
  useEffect(() => {
    dispatch(fetchData());
  }, [dispatch]);
  const data = useSelector((state) => state.fetch.data);
  const user = useSelector((state) => state.user);
  const allUniqueUsers = [...new Set(data.map(user=>user.userId))]


  const [selectedUser, setSelectedUser] = useState('0');
  console.log(selectedUser);

  const changeUser = (user)=>{
    dispatch(addUser(user));
    setSelectedUser(user);
  }

  filter = data.filter((user)=>user.userId == selectedUser)

 // console.log(data);
  //console.log(allUniqueUsers);
  return (
    <div className={style.body}>
      <div className={style.container}>
        {/* heading */}
        <div className={style.heading}>
          <span>
            <h2>TodoList</h2>
          </span>
          <span>
            <label>Select User</label>
            <select onClick={(e)=>changeUser(e.target.value)}>
              {allUniqueUsers.map((u)=>(
                <option>{u}</option>
              ))}
            </select>
          </span>
        </div>
        <div className={style.todoBox}>
          {/* todo list */}



        </div>
      </div>
    </div>
  );
}

export default App;
