import { useSelector, useDispatch } from "react-redux";
import { fetchData, postData } from "./redux/reducer/fetchReducer";
import { useEffect, useState } from "react";
import style from "./app.module.css";
import { addUser } from "./redux/reducer/userReducer";

function App() {
  const dispatch = useDispatch();

  // Fetching the data
  useEffect(() => {
    dispatch(fetchData());
    dispatch(postData())
  }, [dispatch]);

  // Selectors
  const data = useSelector((state) => state.fetch.data);
  const allUniqueUsers = [...new Set(data.map(user => user.userId))];
  const selectedUser = useSelector((state) => state.user);

  // State for selected user
  const [selectedUserId, setSelectedUserId] = useState('0');

  // Function to change user
  const changeUser = (userId) => {
    dispatch(addUser(userId));
    setSelectedUserId(userId);
  }

  // Filter the data based on the selected user
  const filteredData = data.filter((user) => user.userId === Number(selectedUserId));

  return (
    <div className={style.body}>
      <div className={style.container}>
        {/* Heading */}
        <div className={style.heading}>
          <h2>TodoList</h2>
          <div>
            <label>Select User: </label>
            <select value={selectedUser} onChange={(e) => changeUser(e.target.value)}>
              <option value="0">All Users</option>
              {allUniqueUsers.map((userId) => (
                <option key={userId} value={userId}>{userId}</option>
              ))}
            </select>
          </div>
        </div>


        {/* add todo */}
