                <form onSubmit={()=>creat} method="POST">


              <input type="text" className={style.input} placeholder="Enter The todo"/> <submit>POST</submit>

                </form>
              </div>

        {/* Todo list */}
        <div className={style.todoBox}>
          {filteredData.map((user) => (
            <div key={user.id} className={style.todoItem}>
              <h3>{user.title}</h3>
              <input 
                type="radio" 
                id={user.id} 
                value={user.completed} 
                checked={user.completed} 
                readOnly 
              />
              <label htmlFor={user.id}>Completed</label>
              <span>|</span>
              <h4>Edit</h4>
              <span>|</span>
              <h4>Delete</h4>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
