import { createSlice } from "@reduxjs/toolkit";
import { createAsyncThunk } from "@reduxjs/toolkit";

const fetchData = createAsyncThunk('fetchData', async()=>{
    const response = await fetch('https://jsonplaceholder.typicode.com/todos')
    return response.json();
},);

const postData = createAsyncThunk('postData',async ()=>{
  const response =   await fetch('https://jsonplaceholder.typicode.com/posts',{
        method:'POST',
        body:JSON.stringify({
            title: 'ram',
                        body: 'ram',
                        userId: 1,
        }),
        header:{
            'content-type':'application/json; charset=UTF-8',
        },
    })
      const data = await response.json();
    return data;
})

const fetchReducer = createSlice({
    name:'fetchReducer',
    initialState:{
        data:[],
        loading:false,
        error:null,
    },
    reducers:{},

    extraReducers:(builder)=>{
        //fetch
        builder.addCase(fetchData.fulfilled,(state,action)=>{
            state.data=action.payload;
            state.loading=false;
        });
        builder.addCase(fetchData.pending,(state,action)=>{
            state.loading=true;
            state.error=null;
        });
        builder.addCase(fetchData.rejected,(state,action)=>{
            state.error=action.error.message;
        });
        //post
        builder.addCase(postData.fulfilled,(state,action)=>{
            state.data.push(action.payload);
            state.loading=false
        });
        builder.addCase(postData.pending,(state,action)=>{
            state.loading=true;
            state.error=null;
        })
        builder.addCase(postData.rejected,(state,action)=>{
            state.error=action.error.message;
        })


    }
})

export default fetchReducer.reducer;
export {fetchData,postData};


