import { useSelector, useDispatch } from "react-redux";
import { fetchData } from "./redux/reducer/fetchReducer";
import { useEffect } from "react";
import { useState } from "react";
import style from "./app.module.css";
function App() {
  const dispatch = useDispatch();

  //fetching the data
  useEffect(() => {
    dispatch(fetchData());
  }, [dispatch]);
  const data = useSelector((state) => state.fetch.data);
  const user = useSelector((state) => state.user);
  const allUniqueUsers = [...new Set(data.map(user=>user.userId))]


  const [selectedUser, setSelectedUser] = useState('0');
  console.log(selectedUser);


 // console.log(data);
  //console.log(allUniqueUsers);
  return (
    <div className={style.body}>
      <div className={style.container}>
        {/* title */}
        <div className={style.heading}>
          <span>
            <h2>TodoList</h2>
          </span>
          <span>
            <label>Select User</label>
            <select onClick={(e)=>setSelectedUser(e.target.value)}>
              {allUniqueUsers.map((u)=>(
                <option>{u}</option>
              ))}
            </select>

          </span>
        </div>
      </div>
    </div>
  );
}

export default App;
